import React, { useState } from "react";
import "./App.css"; // 별도의 CSS 파일로 스타일을 관리

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div className="container">
      <header>
        <h1>User Management System</h1>
      </header>

      <div className="user-form">
        <button className="btn add-btn" onClick={openModal}>
          Add User
        </button>
      </div>

      <div id="user-list" className="user-list">
        {/* User list will be dynamically rendered here */}
      </div>

      {isModalOpen && (
        <div id="modal" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <div className="user-form">
              <input type="text" id="new-user-name" placeholder="Name" className="text" />
              <input type="text" id="new-user-email" placeholder="Email" className="text" />
              <input type="number" id="new-user-age" placeholder="Age" className="text" />
              <input type="text" id="new-user-address" placeholder="Address" className="text" />
              <button id="add-user-btn" className="btn add-btn">
                Add User
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
