import React, { useState, useEffect } from "react";
import "./App.css";

const App = () => {
  const [users, setUsers] = useState([]); // ÏÇ¨Ïö©Ïûê Îç∞Ïù¥ÌÑ∞Î•º Í¥ÄÎ¶¨ÌïòÎäî ÏÉÅÌÉú
  const [isModalOpen, setIsModalOpen] = useState(false); // Î™®Îã¨ ÏÉÅÌÉú
  const [newUser, setNewUser] = useState({ name: "", email: "", age: "", address: "" }); // ÏÉà ÏÇ¨Ïö©Ïûê Îç∞Ïù¥ÌÑ∞

  // Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ìï®Ïàò
  const fetchUsers = async () => {
    try {
      const response = await fetch("https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users");
      const data = await response.json();
      setUsers(data); // Í∞ÄÏ†∏Ïò® Îç∞Ïù¥ÌÑ∞Î•º ÏÉÅÌÉúÏóê Ï†ÄÏû•
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  // Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä/ÏàòÏ†ï
  const handleAddUser = async () => {
    if (!newUser.name || !newUser.email || !newUser.age || !newUser.address) {
      alert("All fields are required!");
      return;
    }

    try {
      if (newUser.id) {
        // ÏàòÏ†ï ÏöîÏ≤≠ (PUT)
        const response = await fetch(
          `https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${newUser.id}`,
          {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newUser),
          }
        );
        const updatedUser = await response.json();
        setUsers(users.map((user) => (user.id === updatedUser.id ? updatedUser : user))); // Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
      } else {
        // Ï∂îÍ∞Ä ÏöîÏ≤≠ (POST)
        const response = await fetch("https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newUser),
        });
        const addedUser = await response.json();
        setUsers([...users, addedUser]); // ÏÉàÎ°úÏö¥ ÏÇ¨Ïö©Ïûê Ï∂îÍ∞Ä
      }

      setNewUser({ name: "", email: "", age: "", address: "" }); // ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
      setIsModalOpen(false); // Î™®Îã¨ Îã´Í∏∞
    } catch (error) {
      console.error("Error adding/updating user:", error);
    }
  };

  // Îç∞Ïù¥ÌÑ∞ ÏÇ≠Ï†ú
  const handleDeleteUser = async (id) => {
    try {
      await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`, {
        method: "DELETE",
      });
      setUsers(users.filter((user) => user.id !== id)); // ÏÉÅÌÉúÏóêÏÑú ÏÇ≠Ï†úÎêú Îç∞Ïù¥ÌÑ∞ Ï†úÍ±∞
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  // Îç∞Ïù¥ÌÑ∞ Ìé∏Ïßë
  const handleEditUser = (id) => {
    const editedUser = users.find((user) => user.id === id);
    if (editedUser) {
      setNewUser(editedUser); // ÏÑ†ÌÉùÎêú Îç∞Ïù¥ÌÑ∞Î°ú ÏûÖÎ†• ÌïÑÎìú Ï±ÑÏö∞Í∏∞
      setIsModalOpen(true); // Î™®Îã¨ Ïó¥Í∏∞
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="container">
      <header>
        <h1> üïäÔ∏èPieace MemberüïäÔ∏è</h1>
      </header>

      <div className="user-form">
        <button className="btn add-btn" onClick={() => setIsModalOpen(true)}>
          Add User
        </button>
      </div>

      {/* ÏÇ¨Ïö©Ïûê Î™©Î°ù Î†åÎçîÎßÅ */}
      <div id="user-list" className="user-list">
        {users.map((user) => (
          <div key={user.id} className="user-item">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Age:</strong> {user.age}</p>
            <p><strong>Address:</strong> {user.address}</p>
            <button className="btn edit-btn" onClick={() => handleEditUser(user.id)}>
              Edit
            </button>
            <button className="btn delete-btn" onClick={() => handleDeleteUser(user.id)}>
              Delete
            </button>
          </div>
        ))}
      </div>

      {/* Î™®Îã¨ */}
      {isModalOpen && (
        <div id="modal" className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setIsModalOpen(false)}>
              &times;
            </span>
            <div className="user-form">
              <input
                type="text"
                placeholder="Name"
                className="text"
                value={newUser.name}
                onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
              />
              <input
                type="email"
                placeholder="Email"
                className="text"
                value={newUser.email}
                onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
              />
              <input
                type="number"
                placeholder="Age"
                className="text"
                value={newUser.age}
                onChange={(e) => setNewUser({ ...newUser, age: e.target.value })}
              />
              <input
                type="text"
                placeholder="Address"
                className="text"
                value={newUser.address}
                onChange={(e) => setNewUser({ ...newUser, address: e.target.value })}
              />
              <button className="btn add-btn" onClick={handleAddUser}>
                {newUser.id ? "Update User" : "Add User"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
