import React, { useState, useEffect } from "react";
import "./App.css"; // 스타일

const App = () => {
  const [users, setUsers] = useState([]); // 사용자 데이터를 관리하는 상태
  const [isModalOpen, setIsModalOpen] = useState(false);

  // 데이터 가져오기 함수
  const fetchUsers = async () => {
    try {
      const response = await fetch("https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users");
      const data = await response.json();
      setUsers(data); // 가져온 데이터를 상태에 저장
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  // 컴포넌트 마운트 시 데이터 가져오기
  useEffect(() => {
    fetchUsers();
  }, []);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div className="container">
      <header>
        <h1>User Management System</h1>
      </header>

      <div className="user-form">
        <button className="btn add-btn" onClick={openModal}>
          Add User
        </button>
      </div>

      {/* 사용자 목록 렌더링 */}
      <div id="user-list" className="user-list">
        {users.map((user) => (
          <div key={user.id} className="user-item">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Age:</strong> {user.age}</p>
            <p><strong>Address:</strong> {user.address}</p>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div id="modal" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <div className="user-form">
              <input type="text" id="new-user-name" placeholder="Name" className="text" />
              <input type="text" id="new-user-email" placeholder="Email" className="text" />
              <input type="number" id="new-user-age" placeholder="Age" className="text" />
              <input type="text" id="new-user-address" placeholder="Address" className="text" />
              <button id="add-user-btn" className="btn add-btn">
                Add User
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
